                <div id="edit-neighborhood-form">
                    {/* close button for form */}
                    <button onClick={toggleShowing}>Close</button>
                    <EditItemComponent neighborhood={neighborhood} updateNeighborhood={props.updateNeighborhood} showing={showing}></EditItemComponent>
